  font-size: 16px;
 font-weight: bold;
  cursor: pointer;
  display: block;
}
.departments.mobile .dropdown {
  position: relative;
  display: inline-block;
   z-index: 10;
}
.dropdown-content {
  display: none;
  position: absolute;
  background-color: #f9f9f9;
  min-width: 160px;
  overflow: auto;
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
}

.dropdown-content a {
  color: black;
  padding: 12px 16px;
  text-decoration: none;
  display: block;
}
