settings.custom_liquid:
{% for link in linklists.all-departments.links %}               
{% assign filter_name =  link.title | downcase %}
{% for sublink in link.links %}               
{% assign menu_name =  sublink.title %}
<ul class="filterDiv {{ filter_name }}">                    {% for link in linklists[ menu_name ].links %}

<p class="m-link">                           
<a href="{{ link.url }}">{{ link.title }}</a></p>                    
{% for sublink in link.links %}
<a class="m-m-link"                         href="{{ sublink.url }}">{{ sublink.title }}</a><br>

{% for subsublink in sublink.links %}
<a class="s-m-link"                         href="{{ subsublink.url }}">{{ subsublink.title }}</a><br>                
               {% endfor %}                
               {% endfor %}

               {% endfor %}
</ul>
{% endfor %}
{% endfor %}

settings.custom_css:
.sub-filter  {
display: flow;
width: 100%;
margin-bottom: 0;
text-align: left;
}

.sub-filter  ul {
columns: 4; 
width: 100%;
padding: 1% 2%;
margin: 0;
}
p.m-link  {
width: 100%;
border-bottom: 1px solid #000000;
margin: 20px 0 5px;
}
.m-link  a{
color: #000;
font-weight: 800;
}
a.m-m-link  {
    color: #000;
    font-size: 14px;
    font-weight: 600;
}
a.s-m-link  {
    color: #000;
    font-size: 12px;
    font-weight: normal;
    margin-left: 10px;
}
.filterDiv {
  font-weight: bold;
  float: left; 
  width:295px;
  text-align: left;
  margin: 0 20px 20px 10px;
  display: none;
}
.show {
  display: block;
}
.sub-filter a:hover, .sub-filter a:active, .sub-filter a:focus {
    color: #E10F0F;
}
