settings.custom_css:
.mulch-soil-title {
max-width: 800px;
    width: 95%;
    margin-left: auto;
    margin-right: auto;
}
.mulch-soil-title h2.rich-text__heading.title {
    text-transform: capitalize;
    font-size: 36px;
    font-weight: 600;
    letter-spacing: 0.5px;
}
.mulch-soil-title p {
    text-align: left;
}

.mulch-soil-calc {
    max-width: 800px;
    min-width: 330px;
    width: 100%;
    margin-left: auto;
    margin-right: auto;
    vertical-align: baseline;
    font-weight: inherit;
}

.mulch-soil-calc .area-calc {
     border: 1px solid rgba(0, 0, 0, 0.12);
    padding: 4%;
    margin-bottom: 1.5rem;
    border-radius: 0.5rem;
}
.engage-common-0 {
    margin-bottom: 1rem;
    border-bottom: 1px solid rgba(0, 0, 0, 0.12);
}
span.TypographyBase {
    margin-left: 10px;
}
.paragraph-regular {
    margin-bottom: 0;
    font-weight: 600;
}
.field-group {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: center;
}
.sq-ft-text-wrapper p {
    padding: 0.8rem 1.5rem;
    margin: 3.4rem 0 0;
    background-color: #F5F5F5;
}
.ParagraphResult {
    padding: 0.8rem 1.5rem;
    background-color: #F5F5F5;
    font-size: 1rem;
    line-height: 1.5rem;
    font-weight: 600;
}
.engage-common-1,.engage-common-2,.engage-common-3 {
    display: flex;
    align-items: center;
    margin-bottom: 1.5rem;
    flex-wrap: wrap;
}
.engage-common-1::before {
    content: "1";
    font-size: 1rem;
    font-weight: 400;
    display: flex;
    flex: 0 0 auto;
    -webkit-box-align: center;
    align-items: center;
    -webkit-box-pack: center;
    justify-content: center;
    background-color: #E10F0F;
    color: white;
    height: 2rem;
    width: 2rem;
    border-radius: 100%;
    margin-right: 0.75rem;
}

 .FormControlWrapper {
        margin-bottom: 2rem;
    }

.FormGroupWrapper {
    flex-direction: column;
    display: flex;
    flex-wrap: wrap;
}
.FormGroupWrapper > * {
    margin: 0.25rem 0px;
}
.FormControlLabelWrapper {
    position: relative;
    display: inline-flex;
    flex-direction: row;
    -webkit-box-align: center;
    align-items: center;
    box-sizing: border-box;
}

.gxuzbV input:checked, .gxuzbV input:checked + label {
    cursor: pointer;
}

[type="checkbox"] {
    box-sizing: border-box;
    padding: 0px;
}
button, input {
    overflow: visible;
}


.ButtonBase.size--small .btn-label .icon {
        width: 0.875rem;
        height: 0.875rem;
    }
.ButtonBase .btn-label {
    display: inherit;
    -webkit-box-align: center;
    align-items: center;
    -webkit-box-pack: center;
    justify-content: center;
    width: 100%;
    letter-spacing: 0.015rem;
    font-family: Fellix, Arial, sans-serif;
    font-weight: 600;
}
.eptyGD.size--small .btn-label > .icon {
    width: 1rem;
    height: 1rem;
}
.field-wrapper {
        flex: 0 0 auto;
        min-width: 14rem;;
    }
.SelectFormControl {
    position: relative;
    height: 48px;
    padding: 0px;
    display: flex;
    flex-direction: row;
    -webkit-box-align: center;
    align-items: center;
}
.PTZBD,.SelectFormControl svg.icon {
    height: 1rem;
    width: 1rem;
}
.engage-common-2::before {
    content: "2";
    font-size: 1rem;
    font-weight: 400;
    display: flex;
    flex: 0 0 auto;
    -webkit-box-align: center;
    align-items: center;
    -webkit-box-pack: center;
    justify-content: center;
    background-color: #E10F0F;
    color: white;
    height: 2rem;
    width: 2rem;
    border-radius: 100%;
    margin-right: 0.75rem;
}
.engage-common-4 {
     margin-left: 2.75rem;
     margin-bottom: 2.5rem;
    width: 100%;
    margin: 0;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}
.engage-common-3::before {
    content: "3";
    font-size: 1rem;
    font-weight: 400;
    display: flex;
    flex: 0 0 auto;
    -webkit-box-align: center;
    align-items: center;
    -webkit-box-pack: center;
    justify-content: center;
    background-color: #E10F0F;
    color: white;
    height: 2rem;
    width: 2rem;
    border-radius: 100%;
    margin-right: 0.75rem;
}

.ButtonBase.variant--ghost:enabled, .ButtonBase.variant--ghost.enabled {
    background-color: transparent;
    border-color: transparent;
}
.engage-common-5 {
    display: flex;
    flex-direction: row;
}
.ProductEstimate {
    margin-top: 1rem;
}

.EstimateLawn {
    display: flex;
    align-items: center;
    -webkit-box-pack: justify;
    justify-content: space-between;
    border-bottom: 1px solid rgb(112, 112, 112);
    padding: 1rem 0 0;
    min-width: 280px;
    flex-wrap: wrap;
}
.EstimateResult{
    display: flex;
    align-items: center;
    justify-content: space-between;
    border-bottom: 1px solid rgb(112, 112, 112);
    padding: 1rem 0 0;
    min-width: 280px;
    flex-wrap: wrap;
}
.EstimateBags {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding-bottom: 0.5rem;
    min-width: 280px;
    flex-wrap: wrap;
}
p.VolumeResult {
    margin-right: 24px;
}
.engage-common-6 {
    margin-top: 1.5rem;
}
.VolumeResult {
    color: #E10F0F;
    font-size: 1rem;
}
select.select-input {
border-radius: 8px;
}
input.ButtonBase {
position: absolute;
opacity: 0;
}
.ModalWrapper,  .tooltip-subtitle {
    position: relative;
    display: flex;
    flex-direction: column;
    background-color: #FFF;
    box-shadow: 0px 2px 4px rgba(0,0,0,0.2), 0px 4px 5px rgba(0,0,0,0.14), 0px 1px 10px rgba(0,0,0,0.12);
    width: auto;
    padding: 1%;
    outline: none;
    height: auto;
}
.tooltip-subtitle { 
     font-size: 0.8rem;
     line-height: 1.2rem;
     max-width: 20rem;
     margin: 0 auto;
}
.modal-header h4 {
    font-weight: 600;
    color: rgba(0,0,0,0.88);
    font-size: 0.8rem;
    line-height: 1.5rem;
    margin-left: 1rem;
}
.modal-header:not(.no-line) {
    border-bottom: 0.0625rem solid  rgba(0,0,0,0.12);
}
.engage-common-8  {
     font-size: 0.8rem;
     margin: 1.2rem;
}
.engage-common-8 dt {
    font-weight: 600;
}
.engage-common-8 dd {
     margin: 0;
}
.table-head {
    background-color: #f5f5f5;
}
.TableWrapper h5 {
        font-size: 0.7rem;
        font-weight: 600;
        letter-spacing: 0.015rem;
        line-height: 1rem;
        text-transform: capitalize;
}
ul.common-1, .Table {
        list-style: none;
        font-size: 0.7rem;
        line-height: 1rem;
        padding-left: 0;
        margin: 0;
}
ul.common {
     font-size: 0.7rem;
    letter-spacing: 0.015rem;
    line-height: 1rem;
    padding-left: 0.8rem;
    margin: 0;
    min-width: 4.7rem;
}
.ModalWrapper li {
    margin-bottom: 0;
}
